import React from "react";
import ReactMapboxGl, { Cluster, Marker } from "react-mapbox-gl";
import "mapbox-gl/dist/mapbox-gl.css";
import { stores } from "./vibes";
import Sidebar from "./SideBar";

const Map = ReactMapboxGl({
  accessToken:
    "pk.eyJ1IjoiZGV2b25uZXciLCJhIjoiY2t0bThqZWhiMGRxNzJvbDJwazZzOWtuNCJ9.90vA89Cc_lrejzYlkLhsfw",
});
const styles = {
  clusterMarker: {
    width: 30,
    height: 30,
    borderRadius: "50%",
    backgroundColor: "#51D5A0",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    color: "white",
    border: "2px solid #56C498",
    cursor: "pointer",
  },
  marker: {
    width: 10,
    height: 10,
    borderRadius: "50%",
    backgroundColor: "#E0E0E0",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    border: "2px solid #C9C9C9",
  },
};

export default class App extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      lng: -73.935242,
      lat: 40.73061,
      zoom: 11.2,
      popup: {
        coordinates: undefined,
        total: undefined,
        leaves: undefined,
      },
    };
    this.clusterMarker = this.clusterMarker.bind(this);
    // this.clusterClick = this.clusterClick.bind(this);
    this.onMove = this.onMove.bind(this);
  }
  center = [-73.935242, 40.73061];

  clusterMarker = (coordinates) => (
    <Marker
      key={coordinates.toString()}
      coordinates={coordinates}
      style={styles.clusterMarker}
      // onClick={this.clusterClick.bind(this, coordinates, pointCount, getLeaves)}
    >
      {/* <div>{pointCount}</div> */}
    </Marker>
  );

  clusterClick = (coordinates, total) => {
    this.setState({
      popup: {
        coordinates,
        total,
        leaves: getLeaves(),
      },
    });
  };

  onMove = () => {
    if (this.state.popup) {
      this.setState({ popup: undefined });
    }
  };

  render() {
    const { popup } = this.state;
    return (
      <div>
        <Sidebar />
        <div className="map">
          <Map
            style="mapbox://styles/mapbox/dark-v10"
            containerStyle={{
              height: "100vh",
              width: "100vw",
            }}
            center={[this.state.lng, this.state.lat]}
            zoom={[11.2]}
          >
            <Cluster ClusterMarkerFactory={this.clusterMarker}>
              {stores.features.map((store, key) => (
                <Marker
                  key={key}
                  style={styles.marker}
                  coordinates={store.geometry.coordinates}
                  data-feature={store}
                >
                  <div>{store.properties.text}</div>
                </Marker>
              ))}
            </Cluster>
            {popup && (
              <Popup offset={[0, -50]} coordinates={popup.coordinates}>
                <StyledPopup>
                  {popup.leaves.map((leaf, index) => (
                    <div key={index}>
                      {leaf.props["data-feature"].properties.name}
                    </div>
                  ))}
                  {popup.total > popup.leaves.length ? (
                    <div>And more...</div>
                  ) : null}
                </StyledPopup>
              </Popup>
            )}
          </Map>
        </div>
      </div>
    );
  }
}
