import React from "react";
import ReactMapboxGl, { Cluster, Marker, Popup } from "react-mapbox-gl";
import "mapbox-gl/dist/mapbox-gl.css";
import { stores } from "./vibes";
import Sidebar from "./SideBar";

const Map = ReactMapboxGl({
  accessToken:
    "pk.eyJ1IjoiZGV2b25uZXciLCJhIjoiY2t0bThqZWhiMGRxNzJvbDJwazZzOWtuNCJ9.90vA89Cc_lrejzYlkLhsfw",
});
const styles = {
  clusterMarker: {
    width: 30,
    height: 30,
    borderRadius: "50%",
    backgroundColor: "#51D5A0",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    color: "white",
    border: "2px solid #56C498",
    cursor: "pointer",
  },
  marker: {
    width: 10,
    height: 10,
    borderRadius: "50%",
    backgroundColor: "#E0E0E0",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    border: "2px solid #C9C9C9",
  },
};

export default class App extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      lng: -73.935242,
      lat: 40.73061,
      zoom: 11.2,
      popup: undefined,
    };
    this.clusterClick = this.clusterClick.bind(this);
    this.onMove = this.onMove.bind(this);
  }
  center = [-73.935242, 40.73061];

  clusterClick = (coordinates, name, address) => {
    this.setState({
      popup: {
        coordinates,
        name,
        website,
        address,
      },
    });
    console.log(this.state);
  };

  onMove = () => {
    if (this.state.popup) {
      this.setState({ popup: undefined });
    }
  };

  render() {
    const { popup } = this.state;
    return (
      <div>
        <Sidebar />
        <div className="map">
          <Map
            style="mapbox://styles/mapbox/dark-v10"
            containerStyle={{
              height: "100vh",
              width: "100vw",
            }}
            center={[this.state.lng, this.state.lat]}
            zoom={[11.2]}
            onClick={this.onMove}
          >
            {stores.features.map((store, key) => (
              <div>
                <Marker
                  key={key}
                  style={styles.marker}
                  coordinates={store.geometry.coordinates}
                  data-feature={store}
                  onClick={() =>
                    this.clusterClick(
                      store.geometry.coordinates,
                      store.properties.text,
                      store.properties.address
                    )
                  }
                >
                  <div>{store.properties.text}</div>
                </Marker>
                {/* {popup && (
                  <Popup coordinates={store.geometry.coordinates}>hi</Popup>
                )} */}
              </div>
            ))}
            {popup && (
              <Popup coordinates={popup.coordinates}>
                {this.state.popup.name}
              </Popup>
            )}
          </Map>
        </div>
      </div>
    );
  }
}
