import React from "react";
import mapboxgl from "!mapbox-gl"; // eslint-disable-line import/no-webpack-loader-syntax
import { stores } from "./vibes";

mapboxgl.accessToken =
  "pk.eyJ1IjoiZGV2b25uZXciLCJhIjoiY2t0bThqZWhiMGRxNzJvbDJwazZzOWtuNCJ9.90vA89Cc_lrejzYlkLhsfw";

export default class App extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      lng: -73.935242,
      lat: 40.73061,
      zoom: 11.2,
      popUps: false,
    };
    this.mapContainer = React.createRef();
    this.createPopUp = this.createPopUp.bind(this);
  }
  componentDidMount() {
    const { lng, lat, zoom } = this.state;
    const map = new mapboxgl.Map({
      container: this.mapContainer.current,
      style: "mapbox://styles/mapbox/dark-v10",
      center: [lng, lat],
      zoom: zoom,
    });
    map.on("move", () => {
      this.setState({
        lng: map.getCenter().lng.toFixed(4),
        lat: map.getCenter().lat.toFixed(4),
        zoom: map.getZoom().toFixed(2),
      });
    });

    map.addControl(
      new mapboxgl.GeolocateControl({
        positionOptions: {
          enableHighAccuracy: true,
        },
        trackUserLocation: true,
        showUserHeading: true,
      })
    );

    map.on("load", () => {
      /* Add the data to your map as a layer */
      map.addLayer({
        id: "locations",
        type: "circle",
        source: {
          type: "geojson",
          data: stores,
        },
      });
    });

    function createPopUp(currentFeature) {
      if (this.state.popUps) {
        this.setState({ popUps: false });
      }

      const popup = new mapboxgl.Popup({ closeOnClick: false })
        .setLngLat(currentFeature.geometry.coordinates)
        .setHTML(
          `<h3>The Vibe</h3><h4>${currentFeature.properties.address}</h4>`
        )
        .addTo(map);
    }
  }

  render() {
    const { features } = stores;
    return (
      <div>
        <div className="sidebar2">
          <div className="heading">
            <h1>The Vibes</h1>
          </div>
          <div id="listings" className="listings">
            {features.map((place, id) => {
              return (
                <div className="item" key={id} onClick={this.createPopUp}>
                  <p className="title">{place.text}</p>
                </div>
              );
            })}
          </div>
        </div>
        <div ref={this.mapContainer} className="map" />
      </div>
    );
  }
}
